ruleToc.chapterName:
$.chaptername
@js:
result=result.replace(/^\s+/,'');
vip='{{$.isvip}}'!='0';
lock='{{$.islock}}'!='0';
type='{{$.chaptertype}}'=='1';
num=!type?'{{$.chapterid}}.':'';
if(result.match(/[一二三四五六七八九十百千万\d]+\s*章|^\d+[、\.\s]|chapter\s*\d+/i)){
num=''
}else{num=num}
juan=type?'📖 '+result+' 📖':result;
v=vip&&!type?' · ✐':'';l=lock?'[此章节已锁]':'';
result=num+juan+l+v;
##\.\d+

